import './index.css'

const FiltersGroup = props => {
  const {
    ratingsList,
    categoryOptions,
    changeSearch,
    changeCategory,
    changeRating,
  } = props

  const filterByCategory = event => {
    changeCategory(event.target.id)
  }

  const filterByRating = event => {
    changeRating(event.currentTarget.id)
  }

  const filterBySearch = event => {
    if (event.key === 'Enter') {
      changeSearch(event.target.value)
    }
  }

  return (
    <div className="filters-group-container">
      <input
        className="product-search"
        type="search"
        onKeyPress={filterBySearch}
        placeholder="Search"
      />
      <div className="category-box">
        <h1>Category</h1>
        <ul className="categories">
          {categoryOptions.map(obj => (
            <button
              key={obj.categoryId}
              id={obj.categoryId}
              onClick={filterByCategory}
              className="category-btn"
              type="button"
            >
              {obj.name}
            </button>
          ))}
        </ul>
      </div>
      <div className="rating-box">
        <h1>Rating</h1>
        <ul className="ratings">
          {ratingsList.map(obj => (
            <button
              key={obj.ratingId}
              onClick={filterByRating}
              id={obj.ratingId}
              className="rating-btn"
              type="button"
            >
              <img className="rating-img" src={obj.imageUrl} alt="" />{' '}
              <span>& up</span>
            </button>
          ))}
        </ul>
      </div>
      <button type="button" className="clr-filter-btn">Clear Filters</button>
    </div>
  )
}
export default FiltersGroup
